import shallowEqual from './shallow-equal';
import isPlainObject from 'lodash/isPlainObject';

export default function validitiesEqual(validityA, validityB) {
  console.log(validityA, validityB);
  if (isPlainObject(validityA)
    || Array.isArray(validityA)
    || isPlainObject(validityB)
    || Array.isArray(validityB)) {
    return shallowEqual(validityA, validityB);
  }

  // If both validities are truthy/falsey, they are equal.
  return !!validityA === !!validityB;
}
